'use strict';

module.exports = function(controls) {
    const row = document.createElement('div');
    row.className = 'controls';

    Object.keys(controls).forEach(function(key){
        const a = document.createElement('a');
        a.innerHTML = key;
        a.onclick = function(e) {
            e.preventDefault();
            controls[key](e);
            return false;
        }
        row.appendChild(a);
    });

   return row;
};
